import React, { useContext, useState, useReducer } from 'react';
import { Helmet } from 'react-helmet-async';
import Form from 'react-bootstrap/Form';
import { Store } from '../Store';
import Button from 'react-bootstrap/esm/Button';
import { getError } from '../utils';
import { toast } from 'react-toastify'
import axios from 'axios';

const reducer = (state, action) => {
  switch(action.type){
    case 'UPDATE_REQUEST':
      return {...state, loadingUpdate: true}
    case 'UPDATE_SUCCESS':
      return {...state, loadingUpdate: false}
    case 'UPDATE_FAIL':
      return {...state, loadingUpdate: false}

      default:
        return state
  }
}

export default function ProfileScreen() {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;
  const [name, setName] = useState(userInfo.name);
  const [email, setEmail] = useState(userInfo.email);
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const [{loadingUpdate}, dispatch] = useReducer(reducer, {
    loadingUpdate: false
  })

  const submitHandler = async (e) => {
    try{
    e.preventDefault()
    const {data} = await axios.put(
      '/api/users/profile',
      {
        name,
        email,
        password
      },
      {
        headers:{Authorization: `Bearer ${userInfo.token}`}
      }
    )

    dispatch({
      type: 'UPDATE_SUCCESS'
    })
    }catch(err){
      dispatch({
        type: 'FETCH_FAIL'
      })
      toast.error(getError(err))
    }
  };

  return (
    <div className="container small-container">
      <Helmet>
        <title>User Profile</title>
      </Helmet>
      <h1 className="my-3">User Profile</h1>
      <form onSubmit={submitHandler}>
        <Form.Group className="mb-3" controleId="name">
          <Form.Label>Name</Form.Label>
          <Form.Control
            value={name}
            onChange={(e) => setName(e.target.name)}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3" controleId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            value={email}
            onChange={(e) => setEmail(e.target.email)}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3" controleId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            onChange={(e) => setPassword(e.target.email)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controleId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            onChange={(e) => setConfirmPassword(e.target.email)}
          />
        </Form.Group>
        <div className='mb-3'>
            <Button type='submit'>Update</Button>
        </div>
      </form>
    </div>
  );
}
